mapper.insert:
insert into room
(
	romDate,
	romTitle,
	romPersonnel,
	romNote,  
	mbrSeq
)
values
(
	NOW()
	,#{romTitle}
	,#{romPersonnel}
	,#{romNote}
	,1
)

mapper.update:

	 SELECT romSeq FROM room WHERE romSeq = #{romSeq}

update room
set

	romTitle = #{romTitle},
	romPersonnel = #{romPersonnel},
	romNote = #{romNote}

WHERE romSeq = #{romSeq}
